CREATE TABLE `games` (
  `id` int(11) NOT NULL,
  `team1` tinytext NOT NULL,
  `gameday` varchar(9) NOT NULL,
  `gamedate` date NOT NULL,
  `game1` time NOT NULL,
  `game2` time NOT NULL,
  `division` char(1) NOT NULL,
  `field` tinytext NOT NULL,
  `team2` tinytext NOT NULL,
  `score1_t1` int(11) NOT NULL,
  `score1_t2` int(11) NOT NULL,
  `score2_t1` int(11) NOT NULL,
  `score2_t2` int(11) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `locations` (
  `id` int(11) NOT NULL,
  `name` tinytext NOT NULL,
  `notes` tinytext NOT NULL,
  `address` tinytext NOT NULL,
  `lat` double NOT NULL,
  `lng` double NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `news` (
  `id` int(11) NOT NULL,
  `title` tinytext NOT NULL,
  `author_id` int(11) NOT NULL,
  `postbody` text NOT NULL,
  `visibility` int(11) NOT NULL,
  `when` datetime NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;





CREATE TABLE `pages` (
  `id` int(11) NOT NULL,
  `parent` char(4) NOT NULL,
  `element` tinytext NOT NULL,
  `content` longtext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `scores` (
  `id` int(11) NOT NULL,
  `user` int(11) NOT NULL,
  `game` int(11) NOT NULL,
  `matchid` int(11) NOT NULL,
  `date` timestamp NOT NULL DEFAULT current_timestamp()
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `seasons` (
  `id` int(11) NOT NULL,
  `start` date NOT NULL,
  `end` date NOT NULL,
  `games` longtext COLLATE latin1_general_ci NOT NULL,
  `teams` longtext COLLATE latin1_general_ci NOT NULL
) ENGINE=MyISAM DEFAULT CHARSET=latin1 COLLATE=latin1_general_ci;








CREATE TABLE `teams` (
  `id` int(11) NOT NULL,
  `teamname` tinytext NOT NULL,
  `coach` tinytext NOT NULL,
  `points` int(11) NOT NULL,
  `crd` int(11) NOT NULL,
  `division` char(1) NOT NULL,
  `orgname` tinytext NOT NULL,
  `orgphone` tinytext NOT NULL,
  `orgaddr` tinytext NOT NULL,
  `layname` tinytext NOT NULL,
  `layphone` tinytext NOT NULL,
  `layemail` tinytext NOT NULL,
  `tpcontent` longtext NOT NULL,
  `tpimg` tinytext NOT NULL,
  `tpslogan` tinytext NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `users` (
  `id` int(11) NOT NULL,
  `username` varchar(16) NOT NULL,
  `password` char(32) NOT NULL,
  `email` tinytext NOT NULL,
  `phone` char(12) NOT NULL,
  `rank` varchar(6) NOT NULL,
  `team` int(11) NOT NULL,
  `nickname` varchar(25) NOT NULL,
  `fname` tinytext NOT NULL,
  `lname` tinytext NOT NULL,
  `prefernick` tinyint(1) NOT NULL,
  `haswaiver` tinyint(4) NOT NULL,
  `teamposition` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;








CREATE TABLE `waivers` (
  `id` int(11) NOT NULL,
  `user` int(11) NOT NULL,
  `wtype` tinytext NOT NULL,
  `wdate` date NOT NULL,
  `signature` tinytext NOT NULL,
  `isminor` tinyint(1) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;







